<?phpnamespace Estrutura\Validator;use Zend\Validator\AbstractValidator;use Zend\Validator\Exception;class Cep extends AbstractValidator {    const INVALID = 'invalid';    const WRONG_LENGTH = 'wrongLength';    /**     * Validation failure message template definitions     *     * @var array     */    protected $messageTemplates = array(        self::INVALID => "O CEP informado é inválido",        self::WRONG_LENGTH => "O CEP informado deve conter 8 números",    );    /**     * Sets validator options     *     * @param  array|Traversable $options     * @throws Exception\InvalidArgumentException     */    public function __construct($options = null) {        parent::__construct($options);    }    /**     * Returns true if and only if $value is greater than min option     *     * @param  mixed $cpf     * @return bool     */    public function isValid($cep) {        $cep = str_replace(['_', '.'], '', $cep);        if (strlen($cep) != 8) {            $this->error(self::WRONG_LENGTH);            return false;        }        if (!preg_match('/[0-9]{8}/', $cep)) {            $this->error(self::INVALID);            return false;        }        return true;    }}