<?phpnamespace Estrutura\Validator;use Zend\Validator\AbstractValidator;use Zend\Validator\Exception;class CpfCnpj extends AbstractValidator{    const INVALID           = 'invalid';    /**     * Validation failure message template definitions     *     * @var array     */    protected $messageTemplates = array(        self::INVALID           => "O CPF/CNPJ informado é inválido",    );    /**     * Sets validator options     *     * @param  array|Traversable $options     * @throws Exception\InvalidArgumentException     */    public function __construct($options = null)    {        parent::__construct($options);    }    /**     * Returns true if and only if $value is greater than min option     *     * @param  mixed $cpf     * @return bool     */    public function isValid($cpfCnpj)    {        $filter =new \Mvc\Filter\Cnpj();        $valor = $filter->filter($cpfCnpj);        switch(strlen($valor))        {            case 11:                $validator = new Cpf();                break;            case 14:                $validator = new Cnpj();                break;            default:                $this->error(self::INVALID);                return false;        }        if(!$validator->isValid($cpfCnpj))        {            $this->error(self::INVALID);            return false;        }        return true;    }}