<?phpnamespace Estrutura\Validator;use Zend\Validator\AbstractValidator;use Zend\Validator\Exception;class Number extends AbstractValidator{    const INVALID           = 'invalid';    /**     * Validation failure message template definitions     *     * @var array     */    protected $messageTemplates = array(        self::INVALID           => "O valor informado não é numérico",    );    /**     * Sets validator options     *     * @param  array|Traversable $options     * @throws Exception\InvalidArgumentException     */    public function __construct($options = null)    {        parent::__construct($options);    }    /**     * Returns true if and only if $value is greater than min option     *     * @param  mixed $cpf     * @return bool     */    public function isValid($number)    {        if(!is_numeric($number)){            $this->error(self::INVALID);            return false;        }        return true;    }}